export function capitalize(name) {
    return name.charAt(0).toUpperCase() + name.slice(1);
}
export const useTs = (typescript, ts, js) => typescript ? ts : js;
export const propsFilter = (props, outputs) => (props.filter((prop) => {
    if (prop.handle === "change" && outputs.length > 0) {
        return prop;
    }
    if (prop.handle === "none") {
        return prop;
    }
}));
export const funcFormat = (name) => {
    return name.split("").reduce((acc, curr) => {
        if (curr === curr.toUpperCase()) {
            acc += " ";
        }
        acc += curr;
        return acc;
    });
};
